import S from "./PopUpTitleWithSlowUnderline.module.css"
import {useState} from "react";
import 'animate.css'
export const PopUpTitleWithSlowUnderline = ({title, text}) => {
    const [showDivider, setShowDivider] = useState(false);
    useState(() => {
        const timer = setTimeout(() => {
            setShowDivider(true);
            clearTimeout(timer)
        }, 500);
    }, [])
    return (
        <>
            <p className={`${S.title} animate__animated animate__bounce`} >{title}</p>
            <div className={S.divider} style={{transform: showDivider ? 'scaleX(1)' : 'scaleX(0)'}}></div>
            <p >{text}</p>
        </>
    )
}
