import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import sample from "./../../assets/Pexels Videos 2334654.mp4";
import sample1 from "./../../assets/Pexels Videos 2098989.mp4";
import sample2 from "./../../assets/video.mp4";
import S from "./SimpleSlider.module.css"
export const SimpleSlider = () => {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,

    };
    return(

            <Slider {...settings}>
                <div>
                    <video autoPlay muted loop className={S.myVideo} >
                        <source src={sample} type="video/mp4"/>
                    </video>
                </div>
                <div>
                    <video autoPlay muted loop className={S.myVideo} >
                        <source src={sample1} type="video/mp4"/>
                    </video>
                </div>
                <div>
                    <video autoPlay muted loop className={S.myVideo} >
                        <source src={sample2} type="video/mp4"/>
                    </video>
                </div>
            </Slider>

    )
}
